if(data.events.isURL()){
	var vars = $.main.variables;
	var opt = {};
	opt.url = vars.events;
	opt.query = { key : vars.api_key, secret : vars.api_secret, id : data.match.id }
	opt.callback = function(err,res){
		if (res){

			var payload = { match : data.match};
			res = res.body.parseJSON(true);

			payload.event = res.data.event;
			$.send('output',payload);
		}
	}
	REQUEST(opt);
}

